mapper.update:
update fs_b_exame
user_no=#{userNo}, name=#{name}, user_account_name=#{userAccountName}, user_account=#{userAccount}, number=#{number}, exame_type=#{exameType}, exame_date=#{exameDate}, exame_num=#{exameNum}, exame_score=#{exameScore}, exame_total=#{exameTotal}, exame_zy=#{exameZy}, valid=#{valid}, state=#{state}, file_id=#{fileId}, file_url=#{fileUrl}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, display_index=#{displayIndex}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select fs_b_exame.id, fs_b_exame.user_no, fs_b_exame.`name`, fs_b_user.user_account_name, fs_b_user.user_account, fs_b_user.grjlbh number, fs_b_exame.exame_type, fs_b_exame.exame_date, fs_b_exame.exame_num, fs_b_exame.exame_score, fs_b_exame.exame_total, fs_b_exame.exame_zy, fs_b_exame.valid, fs_b_exame.state, fs_b_exame.IS_DELETEMARK, fs_b_exame.CREATE_TIME, fs_b_exame.display_index, fs_b_exame.MODIFY_TIME, fs_b_exame.CREATE_USER_ID, fs_b_exame.MODIFY_USER_ID, fs_b_exame.file_id, fs_b_exame.file_url from fs_b_exame inner join fs_b_user on fs_b_user.user_account=fs_b_exame.user_account where 1=1 and fs_b_exame.user_account!='' and fs_b_user.yuanqu =#{fsBExame.yuanqu} and fs_b_exame.id =#{ fsBExame.id} and fs_b_exame.user_no like concat('%', #{ fsBExame.userNo} ,'%') and fs_b_exame.name like concat('%', #{ fsBExame.name} ,'%') and fs_b_exame.user_account_name like concat('%', #{ fsBExame.userAccountName} ,'%') and (fs_b_user.user_account=#{ fsBExame.userAccount} or fs_b_user.user_account_name like concat('%', #{ fsBExame.userAccount} ,'%')) and fs_b_exame.number like concat('%', #{ fsBExame.number} ,'%') and fs_b_exame.exame_type like concat('%', #{ fsBExame.exameType} ,'%')
AND fs_b_exame.exame_date >= #{fsBExame.exameDateFrom}
AND fs_b_exame.exame_date <= #{fsBExame.exameDateTo}
and fs_b_exame.exame_num like concat('%', #{ fsBExame.exameNum} ,'%') and fs_b_exame.exame_score like concat('%', #{ fsBExame.exameScore} ,'%') and fs_b_exame.exame_total like concat('%', #{ fsBExame.exameTotal} ,'%') and fs_b_exame.exame_zy like concat('%', #{ fsBExame.exameZy} ,'%') and fs_b_exame.valid like concat('%', #{ fsBExame.valid} ,'%') and fs_b_exame.state =#{ fsBExame.state} and fs_b_exame.IS_DELETEMARK =#{ fsBExame.isDeletemark}
AND fs_b_exame.CREATE_TIME >= #{fsBExame.createTimeFrom}
AND fs_b_exame.CREATE_TIME <= #{fsBExame.createTimeTo}
and fs_b_user.dept_id in (select dept_id from t_dept where dept_id=#{fsBExame.deptId} or parent_id =#{fsBExame.deptId}) and fs_b_exame.display_index =#{ fsBExame.displayIndex}
AND fs_b_exame.MODIFY_TIME >= #{fsBExame.modifyTimeFrom}
AND fs_b_exame.MODIFY_TIME <= #{fsBExame.modifyTimeTo}
union select fs_b_exame.id, fs_b_exame.user_no, fs_b_exame.`name`, fs_b_user.user_account_name, fs_b_user.user_account, fs_b_user.grjlbh number, fs_b_exame.exame_type, fs_b_exame.exame_date, fs_b_exame.exame_num, fs_b_exame.exame_score, fs_b_exame.exame_total, fs_b_exame.exame_zy, fs_b_exame.valid, fs_b_exame.state, fs_b_exame.IS_DELETEMARK, fs_b_exame.CREATE_TIME, fs_b_exame.display_index, fs_b_exame.MODIFY_TIME, fs_b_exame.CREATE_USER_ID, fs_b_exame.MODIFY_USER_ID, fs_b_exame.file_id, fs_b_exame.file_url from fs_b_exame inner join fs_b_user on fs_b_user.grjlbh=fs_b_exame.number where 1=1 and (fs_b_exame.user_account='' or fs_b_exame.user_account is null) and fs_b_exame.number!='' and fs_b_user.yuanqu =#{fsBExame.yuanqu} and fs_b_exame.id =#{ fsBExame.id} and fs_b_exame.user_no like concat('%', #{ fsBExame.userNo} ,'%') and fs_b_exame.name like concat('%', #{ fsBExame.name} ,'%') and fs_b_exame.user_account_name like concat('%', #{ fsBExame.userAccountName} ,'%') and (fs_b_user.user_account=#{ fsBExame.userAccount} or fs_b_user.user_account_name like concat('%', #{ fsBExame.userAccount} ,'%')) and fs_b_exame.number like concat('%', #{ fsBExame.number} ,'%') and fs_b_exame.exame_type like concat('%', #{ fsBExame.exameType} ,'%')
AND fs_b_exame.exame_date >= #{fsBExame.exameDateFrom}
AND fs_b_exame.exame_date <= #{fsBExame.exameDateTo}
and fs_b_exame.exame_num like concat('%', #{ fsBExame.exameNum} ,'%') and fs_b_exame.exame_score like concat('%', #{ fsBExame.exameScore} ,'%') and fs_b_exame.exame_total like concat('%', #{ fsBExame.exameTotal} ,'%') and fs_b_exame.exame_zy like concat('%', #{ fsBExame.exameZy} ,'%') and fs_b_exame.valid like concat('%', #{ fsBExame.valid} ,'%') and fs_b_exame.state =#{ fsBExame.state} and fs_b_exame.IS_DELETEMARK =#{ fsBExame.isDeletemark}
AND fs_b_exame.CREATE_TIME >= #{fsBExame.createTimeFrom}
AND fs_b_exame.CREATE_TIME <= #{fsBExame.createTimeTo}
and fs_b_user.dept_id in (select dept_id from t_dept where dept_id=#{fsBExame.deptId} or parent_id =#{fsBExame.deptId}) and fs_b_exame.display_index =#{ fsBExame.displayIndex}
AND fs_b_exame.MODIFY_TIME >= #{fsBExame.modifyTimeFrom}
AND fs_b_exame.MODIFY_TIME <= #{fsBExame.modifyTimeTo}

select fs_b_user.user_account,fs_b_exame.exame_type,fs_b_user.grjlbh number,fs_b_exame.exame_num ,fs_b_exame.file_id from fs_b_exame inner join fs_b_user on (fs_b_exame.number= fs_b_user.grjlbh and fs_b_user.user_account is null )or fs_b_exame.user_account=fs_b_user.user_account where ((fs_b_exame.exame_date > DATE_SUB(CURDATE(), interval 2 year) and fs_b_exame.exame_type='卫生考试') or (fs_b_exame.exame_date > DATE_SUB(CURDATE(), interval 5 year) and fs_b_exame.exame_type='环保考试')) and fs_b_user.user_account in
#{item}
order by exame_date desc

