mapper.update:
update mdl_b_surgeryinfo
dept_name=#{deptName}, user_account_name=#{userAccountName}, user_account=#{userAccount}, zhicheng=#{zhicheng}, sex_name=#{sexName}, birthday=#{birthday}, jb=#{jb}, valid_date=#{validDate}, note=#{note}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
SELECT -- `mdl_b_surgeryinfo_d`.`id` AS `id`, -- `mdl_b_surgeryinfo_d`.`code` AS `code`, -- `mdl_b_surgeryinfo_d`.`name` AS `name`, -- `mdl_b_surgeryinfo_d`.`level` AS `level`, -- `mdl_b_surgeryinfo_d`.`lb` AS `lb`, `mdl_b_surgeryinfo`.id AS `id`, `mdl_b_surgeryinfo`.`user_account_name` AS `user_account_name`, `mdl_b_surgeryinfo`.`user_account` AS `user_account`, `mdl_b_surgeryinfo`.`zhicheng` AS `zhicheng`, `mdl_b_surgeryinfo`.`sex_name` AS `sex_name`, `mdl_b_surgeryinfo`.`birthday` AS `birthday`, `mdl_b_surgeryinfo`.`jb` AS `jb`, `mdl_b_surgeryinfo`.`valid_date` AS `valid_date`, `mdl_b_surgeryinfo`.`dept_name` AS `dept_name`, `mdl_b_surgeryinfo`.`note` AS `note` FROM `mdl_b_surgeryinfo` -- JOIN `mdl_b_surgeryinfo_d` ON `mdl_b_surgeryinfo`.`id` = `mdl_b_surgeryinfo_d`.`base_id` where 1=1 and mdl_b_surgeryinfo.IS_DELETEMARK = 1 and mdl_b_surgeryinfo.dept_name like concat('%', #{ mdlBSurgeryinfo.deptName} ,'%')
and ( mdl_b_surgeryinfo.user_account = #{ mdlBSurgeryinfo.userAccount} or mdl_b_surgeryinfo.user_account_name like concat('%', #{ mdlBSurgeryinfo.userAccount} ,'%') )
and mdl_b_surgeryinfo.zhicheng like concat('%', #{ mdlBSurgeryinfo.zhicheng} ,'%') and mdl_b_surgeryinfo.sex_name like concat('%', #{ mdlBSurgeryinfo.sexName} ,'%') and mdl_b_surgeryinfo.birthday like concat('%', #{ mdlBSurgeryinfo.birthday} ,'%') and mdl_b_surgeryinfo.jb like concat('%', #{ mdlBSurgeryinfo.jb} ,'%')
AND mdl_b_surgeryinfo.valid_date >= #{mdlBSurgeryinfo.validDateFrom}
AND mdl_b_surgeryinfo.valid_date <= #{mdlBSurgeryinfo.validDateTo}
AND mdl_b_surgeryinfo.valid_date in ( SELECT MAX(valid_date) from mdl_b_surgeryinfo b where b.valid_date <= #{mdlBSurgeryinfo.validDatePot} and b.user_account= mdl_b_surgeryinfo.user_account GROUP BY user_account)
and mdl_b_surgeryinfo.note like concat('%', #{ mdlBSurgeryinfo.note} ,'%') and mdl_b_surgeryinfo.state =#{ mdlBSurgeryinfo.state}
AND mdl_b_surgeryinfo.CREATE_TIME >= #{mdlBSurgeryinfo.createTimeFrom}
AND mdl_b_surgeryinfo.CREATE_TIME <= #{mdlBSurgeryinfo.createTimeTo}
AND mdl_b_surgeryinfo.MODIFY_TIME >= #{mdlBSurgeryinfo.modifyTimeFrom}
AND mdl_b_surgeryinfo.MODIFY_TIME <= #{mdlBSurgeryinfo.modifyTimeTo}

