mapper.update:
update mdl_d_mdt_old
dept_new=#{deptNew}, fzr=#{fzr}, zhicheng=#{zhicheng}, cy1=#{cy1}, cy2=#{cy2}, cy3=#{cy3}, cy4=#{cy4}, cy5=#{cy5}, cy6=#{cy6}, cy7=#{cy7}, cy8=#{cy8}, cy9=#{cy9}, cy10=#{cy10}, xmmc=#{xmmc}, year=#{year}, xmsp=#{xmsp}, state=#{state}, IS_DELETEMARK=#{isDeletemark}, CREATE_TIME=#{createTime}, MODIFY_TIME=#{modifyTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId},
where id=#{id}

mapper.select:
select * from mdl_d_mdt_old where 1=1 and mdl_d_mdt_old.dept_new like concat('%', #{ mdlDMdtOld.deptNew} ,'%') and mdl_d_mdt_old.fzr like concat('%', #{ mdlDMdtOld.fzr} ,'%') and mdl_d_mdt_old.zhicheng like concat('%', #{ mdlDMdtOld.zhicheng} ,'%') and mdl_d_mdt_old.cy1 like concat('%', #{ mdlDMdtOld.cy1} ,'%') and mdl_d_mdt_old.cy2 like concat('%', #{ mdlDMdtOld.cy2} ,'%') and mdl_d_mdt_old.cy3 like concat('%', #{ mdlDMdtOld.cy3} ,'%') and mdl_d_mdt_old.cy4 like concat('%', #{ mdlDMdtOld.cy4} ,'%') and mdl_d_mdt_old.cy5 like concat('%', #{ mdlDMdtOld.cy5} ,'%') and mdl_d_mdt_old.cy6 like concat('%', #{ mdlDMdtOld.cy6} ,'%') and mdl_d_mdt_old.cy7 like concat('%', #{ mdlDMdtOld.cy7} ,'%') and mdl_d_mdt_old.cy8 like concat('%', #{ mdlDMdtOld.cy8} ,'%') and mdl_d_mdt_old.cy9 like concat('%', #{ mdlDMdtOld.cy9} ,'%') and mdl_d_mdt_old.cy10 like concat('%', #{ mdlDMdtOld.cy10} ,'%') and mdl_d_mdt_old.xmmc like concat('%', #{ mdlDMdtOld.xmmc} ,'%') and mdl_d_mdt_old.year like concat('%', #{ mdlDMdtOld.year} ,'%') and mdl_d_mdt_old.xmsp like concat('%', #{ mdlDMdtOld.xmsp} ,'%') and mdl_d_mdt_old.state =#{ mdlDMdtOld.state} and mdl_d_mdt_old.IS_DELETEMARK =#{ mdlDMdtOld.isDeletemark}
AND mdl_d_mdt_old.CREATE_TIME >= #{mdlDMdtOld.createTimeFrom}
AND mdl_d_mdt_old.CREATE_TIME <= #{mdlDMdtOld.createTimeTo}
AND mdl_d_mdt_old.MODIFY_TIME >= #{mdlDMdtOld.modifyTimeFrom}
AND mdl_d_mdt_old.MODIFY_TIME <= #{mdlDMdtOld.modifyTimeTo}
