mapper.select:
select  *  from view_surgery
where  1=1
     and  view_surgery.dept_name like concat('%', #{ viewSurgery.deptName} ,'%')
     and  view_surgery.user_account_name like concat('%', #{ viewSurgery.userAccountName} ,'%')
     and  view_surgery.user_account like concat('%', #{ viewSurgery.userAccount} ,'%')
     and  view_surgery.zhicheng like concat('%', #{ viewSurgery.zhicheng} ,'%')
     and  view_surgery.sex_name like concat('%', #{ viewSurgery.sexName} ,'%')
     and  view_surgery.birthday like concat('%', #{ viewSurgery.birthday} ,'%')
     and  view_surgery.jb like concat('%', #{ viewSurgery.jb} ,'%')

    AND view_surgery.valid_date  >=   #{viewSurgery.validDateFrom}


    AND  view_surgery.valid_date  <=   #{viewSurgery.validDateTo}

     and  view_surgery.note like concat('%', #{ viewSurgery.note} ,'%')
 and  view_surgery.state =#{ viewSurgery.state} 
 and  view_surgery.IS_DELETEMARK =#{ viewSurgery.isDeletemark} 

    AND view_surgery.CREATE_TIME  >=   #{viewSurgery.createTimeFrom}


    AND  view_surgery.CREATE_TIME  <=   #{viewSurgery.createTimeTo}


    AND view_surgery.MODIFY_TIME  >=   #{viewSurgery.modifyTimeFrom}


    AND  view_surgery.MODIFY_TIME  <=   #{viewSurgery.modifyTimeTo}
