mapper.update:
update xxb_b_check
yuanqu=#{yuanqu}, dept_new=#{deptNew}, project_name=#{projectName}, project_level=#{projectLevel}, project_type=#{projectType}, project_lb=#{projectLb}, isxzyljs=#{isxzyljs}, applydat=#{applydat}, user_account_name=#{userAccountName}, user_account=#{userAccount}, yggh=#{yggh}, sex_name=#{sexName}, birthday=#{birthday}, userdept=#{userdept}, edu=#{edu}, zhicheng=#{zhicheng}, zhichenglc=#{zhichenglc}, zhiwu=#{zhiwu}, zhuany=#{zhuany}, zhuanc=#{zhuanc}, telephone=#{telephone}, lev=#{lev}, typ=#{typ}, projectcontent=#{projectcontent}, lincaqfx=#{lincaqfx}, lincyyfx=#{lincyyfx}, lincjssb=#{lincjssb}, syz=#{syz}, jjz=#{jjz}, czgz=#{czgz}, srtdat=#{srtdat}, enddat=#{enddat}, yanzhan_date=#{yanzhanDate}, zq_date=#{zqDate}, mq_date=#{mqDate}, archive_id=#{archiveId}, file_id=#{fileId}, file_url=#{fileUrl}, ycdat=#{ycdat}, shstate=#{shstate}, xmjdstate=#{xmjdstate}, COMMENTS=#{comments}, STATE=#{state}, IS_DELETEMARK=#{isDeletemark}, MODIFY_TIME=#{modifyTime}, CREATE_TIME=#{createTime}, CREATE_USER_ID=#{createUserId}, MODIFY_USER_ID=#{modifyUserId}, projectmdyycx=#{projectmdyycx}, gnwyjyygk=#{gnwyjyygk}, kxxfxssjh=#{kxxfxssjh}, projectlcwtxyfx=#{projectlcwtxyfx}, ryjjpxgwzzqk=#{ryjjpxgwzzqk}, blhgbfzffcs=#{blhgbfzffcs}, zkcs=#{zkcs}, sfwyzlkmfw=#{sfwyzlkmfw}, flownum=#{flownum},
where id=#{id}

mapper.select:
select  *  from xxb_b_check
where  IS_DELETEMARK=1
     and  xxb_b_check.id =#{ xxbBCheck.id} 
     and  xxb_b_check.dept_new like concat('%', #{ xxbBCheck.deptNew} ,'%')
     and  xxb_b_check.project_name like concat('%', #{ xxbBCheck.projectName} ,'%')
 and  xxb_b_check.project_level =#{ xxbBCheck.projectLevel} 
 and  xxb_b_check.project_type =#{ xxbBCheck.projectType} 
 and  xxb_b_check.yuanqu =#{ xxbBCheck.yuanqu} 
 and  xxb_b_check.project_lb =#{ xxbBCheck.projectLb} 
 and  xxb_b_check.isxzyljs =#{ xxbBCheck.isxzyljs} 

    AND xxb_b_check.applydat  >=   #{xxbBCheck.applydatFrom}


   and xxb_b_check.CREATE_USER_ID =#{xxbBCheck.createUserId}


    AND  xxb_b_check.applydat  <=   #{xxbBCheck.applydatTo}

     and  xxb_b_check.user_account_name like concat('%', #{ xxbBCheck.userAccountName} ,'%')
     and  xxb_b_check.user_account like concat('%', #{ xxbBCheck.userAccount} ,'%')

        and xxb_b_check.id in

            #{item}


     and  xxb_b_check.sex_name like concat('%', #{ xxbBCheck.sexName} ,'%')
     and  xxb_b_check.birthday like concat('%', #{ xxbBCheck.birthday} ,'%')
     and  xxb_b_check.userdept like concat('%', #{ xxbBCheck.userdept} ,'%')
     and
        xxb_b_check.id in (select b.pid from xxb_b_deptflow b where b.STATE=0 and b.flow_account!='ywcgl'  )

     and  xxb_b_check.zhicheng like concat('%', #{ xxbBCheck.zhicheng} ,'%')
     and  xxb_b_check.zhichenglc like concat('%', #{ xxbBCheck.zhichenglc} ,'%')
     and  xxb_b_check.zhiwu like concat('%', #{ xxbBCheck.zhiwu} ,'%')
     and  xxb_b_check.zhuany like concat('%', #{ xxbBCheck.zhuany} ,'%')
     and  xxb_b_check.zhuanc like concat('%', #{ xxbBCheck.zhuanc} ,'%')
     and  xxb_b_check.telephone like concat('%', #{ xxbBCheck.telephone} ,'%')
     and  xxb_b_check.projectcontent like concat('%', #{ xxbBCheck.projectcontent} ,'%')
     and  xxb_b_check.lincaqfx like concat('%', #{ xxbBCheck.lincaqfx} ,'%')
     and  xxb_b_check.lincyyfx like concat('%', #{ xxbBCheck.lincyyfx} ,'%')
     and  xxb_b_check.lincjssb like concat('%', #{ xxbBCheck.lincjssb} ,'%')
     and  xxb_b_check.syz like concat('%', #{ xxbBCheck.syz} ,'%')
     and  xxb_b_check.jjz like concat('%', #{ xxbBCheck.jjz} ,'%')
     and  xxb_b_check.czgz like concat('%', #{ xxbBCheck.czgz} ,'%')

    AND xxb_b_check.srtdat  >=   #{xxbBCheck.srtdatFrom}


    AND  xxb_b_check.srtdat  <=  concat(#{xxbBCheck.srtdatTo},' 23:59:59')


    AND xxb_b_check.enddat  >=   #{xxbBCheck.enddatFrom}


    AND  xxb_b_check.enddat  <=   concat(#{xxbBCheck.enddatTo},' 23:59:59')


    AND xxb_b_check.yanzhan_date  >=   #{xxbBCheck.yanzhanDateFrom}


    AND  xxb_b_check.yanzhan_date  <=   concat(#{xxbBCheck.yanzhanDateTo}, ' 23:59:59')


    AND xxb_b_check.zq_date  >=   #{xxbBCheck.zqDateFrom}


    AND  xxb_b_check.zq_date  <=  concat( #{xxbBCheck.zqDateTo}, ' 23:59:59')


    AND xxb_b_check.mq_date  >=   #{xxbBCheck.mqDateFrom}


    AND  xxb_b_check.mq_date  <=   concat( #{xxbBCheck.mqDateTo}, ' 23:59:59')

     and  xxb_b_check.archive_id like concat('%', #{ xxbBCheck.archiveId} ,'%')
     and  xxb_b_check.file_id like concat('%', #{ xxbBCheck.fileId} ,'%')
     and  xxb_b_check.file_url like concat('%', #{ xxbBCheck.fileUrl} ,'%')

    AND xxb_b_check.ycdat  >=   #{xxbBCheck.ycdatFrom}


    AND  xxb_b_check.ycdat  <=   #{xxbBCheck.ycdatTo}


   and  (xxb_b_check.shstate is NULL or xxb_b_check.shstate=0)



    and  xxb_b_check.shstate =#{xxbBCheck.shstate}


 and  xxb_b_check.xmjdstate =#{ xxbBCheck.xmjdstate} 
     and  xxb_b_check.COMMENTS like concat('%', #{ xxbBCheck.comments} ,'%')
 and  xxb_b_check.STATE =#{ xxbBCheck.state} 
 and  xxb_b_check.IS_DELETEMARK =#{ xxbBCheck.isDeletemark} 

    AND xxb_b_check.MODIFY_TIME  >=   #{xxbBCheck.modifyTimeFrom}


    AND  xxb_b_check.MODIFY_TIME  <=   #{xxbBCheck.modifyTimeTo}


    AND xxb_b_check.CREATE_TIME  >=   #{xxbBCheck.createTimeFrom}


    AND  xxb_b_check.CREATE_TIME  <=   #{xxbBCheck.createTimeTo}

     and  xxb_b_check.projectmdyycx like concat('%', #{ xxbBCheck.projectmdyycx} ,'%')
     and  xxb_b_check.gnwyjyygk like concat('%', #{ xxbBCheck.gnwyjyygk} ,'%')
     and  xxb_b_check.kxxfxssjh like concat('%', #{ xxbBCheck.kxxfxssjh} ,'%')
     and  xxb_b_check.projectlcwtxyfx like concat('%', #{ xxbBCheck.projectlcwtxyfx} ,'%')
     and  xxb_b_check.ryjjpxgwzzqk like concat('%', #{ xxbBCheck.ryjjpxgwzzqk} ,'%')
     and  xxb_b_check.blhgbfzffcs like concat('%', #{ xxbBCheck.blhgbfzffcs} ,'%')
     and  xxb_b_check.zkcs like concat('%', #{ xxbBCheck.zkcs} ,'%')
     and  xxb_b_check.sfwyzlkmfw like concat('%', #{ xxbBCheck.sfwyzlkmfw} ,'%')
 and  xxb_b_check.flownum =#{ xxbBCheck.flownum}

select DISTINCT m.user_account,m.user_account_name,m.telephone from xxb_b_deptflow a,xxb_b_check b,sdl_b_user_mg m where a.pid=b.id and b.STATE=1 and a.flownum=b.flownum and b.user_account=m.user_account and a.flow_account!='ywcgl' and a.STATE=0
